import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent {
  title = 'My Angulat todos app';


  isAlert = false;
  toogleAlert() {
    this.isAlert = !this.isAlert;
  }
}
<div class="continer">
  <h1>{{ title }}</h1>
  <app-todos></app-todos>
  </div>
