import { Component } from '@angular/core';
import { ITodo } from 'src/app/models/todo';

@Component({
  selector: 'app-todos',
  templateUrl: './todos.component.html',
  styleUrls: ['./todos.component.scss'],
})
export class TodosComponent {
  todosItem = [
    { id: 1, text: 'todo1', completed: false },
    { id: 2, text: 'todo2', completed: false },
    { id: 3, text: 'todo3', completed: false },
  ];

  

  toggleComplete(todo: ITodo) {
    todo.completed = !todo.completed;
  }
  delTodo(todo: ITodo) {
    this.todosItem = this.todosItem.filter((itemTodo) => todo !== itemTodo);
  }
  addTodo(text: string) {

  }
private getId(todos: ITodo): number {
  return todos
}

}
<div class="todos-app">
  <!-- <h2 class="is-empty-text" [class.isEmpty]="todosItem.length">
    Todo list is empty ...
  </h2> -->
  <!-- <h2 *ngIf="!todosItem.length" class="is-empty-text" >
    Todo list is empty ...
  </h2> -->
  <div class="add-todo">
    <input type="text" class="add-todo__input" />
    <button class="add-todo__btn">add todo</button>
  </div>

  <div *ngIf="!todosItem.length; then thenBlock; else elseBlock"></div>

  <ng-template #thenBlock>
    <h2 class="is-empty-text">Todo list is empty ...</h2>
  </ng-template>

  <ng-template #elseBlock>
    <div class="todo">
      <h2 class="todos__count">{{ todosItem.length }}</h2>
      <ul class="todo-list">
        <app-todo-item
          *ngFor="let todo of todosItem; let i = index"
          [todo]="todo"
          [index]="i"
          (toggleComplete)="toggleComplete(todo)"
          (delTodo)="delTodo(todo)"
        >
        </app-todo-item>
      </ul>
    </div>
  </ng-template>
</div>
