import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent {
  title = 'My Angulat todos app';
  todosItem = [
    { id: 1, text: 'todo1', completed: false },
    { id: 2, text: 'todo2', completed: false },
    { id: 3, text: 'todo3', completed: false },
  ];

  isAlert = false;
  toogleAlert() {
    this.isAlert = !this.isAlert;
  }
}
<div class="continer">
  <h1>{{ title }}</h1>

  <div class="todos-app">
    <ul class="todo-list" *ngFor="let todo of todosItem; let i = index">
      <input type="checkbox" [checked] = "isAlert" (change)="toogleAlert()">
      <li class="todo-item">
        <span>{{ i + 1 }}. </span>
        <span>{{ todo.text }}</span>
      </li>
    </ul>
  </div>

  <div [class]="isAlert ? 'info alert' : 'info'">

  </div>
  <button (click)="toogleAlert()">alert</button>
</div>
