import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent {
  title = 'My Angulat todos app';


  isAlert = false;
  toogleAlert() {
    this.isAlert = !this.isAlert;
  }
}
<div class="continer">
  <h1>{{ title }}</h1>
  <app-todos></app-todos>
  <div class="todos-app">
    <ul class="todo-list" *ngFor="let todo of todosItem; let i = index">
      <li class="todo-item" [class]="isAlert ? 'info alert' : 'info'">
        <input type="checkbox" [checked]="isAlert" (change)="toogleAlert()" />
        <span>{{ i + 1 }}. </span>
        <span>{{ todo.text }}</span>
      </li>
    </ul>
  </div>

  <div [class]="isAlert ? 'info alert' : 'info'"></div>
  <button (click)="toogleAlert()">alert</button>
</div>
