import { Component, Input, Output, EventEmitter } from '@angular/core';
import { ITodo } from 'src/app/models/todo';

@Component({
  selector: 'app-edit-todo',
  templateUrl: './app-edit-todo.component.html',
  styleUrls: ['./app-edit-todo.component.scss'],
})
export class AppEditTodoComponent {
  @Input() todo: ITodo;
 @Input() todoTextEdit: string


 @Output() editTodo: EventEmitter<string> = new EventEmitter() 




  onEditTodo() {

    this.editTodo.emit("text")
    this.isEdit = false
  }



}
<div>
  <input type="text" [(ngModel)]="todoTextEdit" class="edit-todo__input" />
</div>
