import { Component, Input, Output, EventEmitter } from '@angular/core';
import { ITodo } from 'src/app/models/todo';

@Component({
  selector: 'app-todo-item',
  templateUrl: './todo-item.component.html',
  styleUrls: ['./todo-item.component.scss'],
})
export class TodoItemComponent {
  @Input() todo: ITodo;
  @Input() index: number;

  @Output() toggleComplete: EventEmitter<void> = new EventEmitter();
  @Output() delTodo: EventEmitter<void> = new EventEmitter();
  @Output() editTodo: EventEmitter<{ text: string; id: number }> =
    new EventEmitter();

  isEdit = false;
  todoTextEdit: string;

  ngOnInit() {
    if (this.todo) {
      this.todoTextEdit = this.todo.title;
    }
  }
  onToggleComplete() {
    this.toggleComplete.emit();
  }

  onDelTodo() {
    this.delTodo.emit();
  }

  onEditTodo() {
    this.editTodo.emit({ text: this.todoTextEdit, id: this.todo.id });
    this.isEdit = false;
  }
}
<div>
  <li [class]="!todo.completed ? 'todo-item' : 'todo-item todo-item_completed'">
    <span>{{ index + 1 }}. </span>
    <input
      type="checkbox"
      [checked]="todo.completed"
      (change)="onToggleComplete()"
    />
    <span>{{ todo.title }}</span>
    <button class="todo-item__del-btn" (click)="onDelTodo()">&times;</button>
    <button
      *ngIf="!isEdit; else editButton"
      (click)="isEdit = !isEdit"
      class="todo-item__edit-btn"
    >
      Edit
    </button>
    <ng-template #editButton>
      <button (click)="onEditTodo()" class="todo-item__edit-btn">Save</button>
    </ng-template>
  </li>
  <div>
    <input
      *ngIf="isEdit"
      type="text"
      [(ngModel)]="todoTextEdit"
      class="edit-todo__input"
    />
  </div>
  <!-- <app-edit-todo *ngIf="isEdit" [todo]="todo" [todoTextEdit]="todoTextEdit" class="todo-edit" (onEditTodo)="onEditTodo()"></app-edit-todo> -->
</div>
